import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import StockPredictionApp from './App';
import { Chart, CandlestickController, CategoryScale, LinearScale, TimeScale, Tooltip } from 'chart.js';
import 'chartjs-chart-financial';

document.addEventListener('DOMContentLoaded', () => {
  const root = document.getElementById('root');
  const loader = document.getElementById('loader');
  const stockBg = document.getElementById('stock-bg');

  // Simulating a loading delay for dramatic effect
  setTimeout(() => {
    ReactDOM.render(
      <React.StrictMode>
        <StockPredictionApp />
      </React.StrictMode>,
      root,
      () => {
        if (loader) {
          loader.style.opacity = '0';
          loader.style.transition = 'opacity 0.5s ease-out';
          setTimeout(() => {
            loader.style.display = 'none';
          }, 500);
        }
        root.style.display = 'block';

        // Add market insights dynamically
        addMarketInsights();

        // Attach stock selector change handler
        attachStockSelectorListener();
      }
    );
  }, 2000); // 2 second delay

  // Add parallax effect to the background
  document.addEventListener('mousemove', (e) => {
    const moveX = (e.clientX - window.innerWidth / 2) * 0.01;
    const moveY = (e.clientY - window.innerHeight / 2) * 0.01;
    stockBg.style.transform = `translate(${moveX}px, ${moveY}px)`;
  });
});

function addMarketInsights() {
  const insights = [
    "Global markets show mixed trends amid economic recovery hopes.",
    "Tech sector continues to outperform, driven by AI and cloud computing.",
    "Oil prices stabilize as OPEC+ agrees on production cuts.",
    "Cryptocurrency market experiences high volatility; experts urge caution.",
    "Emerging markets attract investors seeking higher yields."
  ];

  const insightsList = document.getElementById('insights-list');
  insights.forEach(insight => {
    const li = document.createElement('li');
    li.textContent = insight;
    insightsList.appendChild(li);
  });
}

// Function to listen for stock selection and render the candlestick chart
function attachStockSelectorListener() {
  const stockSelector = document.querySelector('.stock-selector');

  stockSelector.addEventListener('change', async (event) => {
    const selectedStock = event.target.value;
    if (selectedStock !== "Select a stock") {
      const stockData = await fetchStockData(selectedStock);
      renderCandlestickChart(stockData);
    }
  });
}

// Fetch stock data from the backend API
async function fetchStockData(stock) {
  const response = await fetch(`/api/stock/${stock}`);
  const data = await response.json();
  return data.map(d => ({
    t: new Date(d.Date),
    o: d.Open,
    h: d.High,
    l: d.Low,
    c: d.Close
  }));
}

// Render the candlestick chart
function renderCandlestickChart(data) {
  const ctx = document.getElementById('stockChart').getContext('2d');
  new Chart(ctx, {
    type: 'candlestick',
    data: {
      datasets: [{
        label: 'Candlestick Chart',
        data: data,
        borderColor: 'rgba(0, 255, 136, 0.8)',
        backgroundColor: 'rgba(255, 65, 54, 0.5)'
      }]
    },
    options: {
      scales: {
        x: {
          type: 'time',
          time: {
            unit: 'day'
          }
        },
        y: {
          beginAtZero: false
        }
      }
    }
  });
}
