import React, { useState } from 'react';
import './App.css';

function App() {
    const [stockSymbol, setStockSymbol] = useState('');
    const [predictions, setPredictions] = useState([]);
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);

    const fetchStockPrediction = () => {
        if (!stockSymbol) {
            alert("Please enter a stock symbol.");
            return;
        }

        setLoading(true);  // Start loading

        fetch(`http://localhost:5001/get_stock_prediction?stock_symbol=${stockSymbol}`)
            .then(response => response.json())
            .then(data => {
                setLoading(false);  // Stop loading
                if (data.length === 0) {
                    setError('No data found for this stock symbol.');
                    setPredictions([]);
                } else {
                    setError('');
                    setPredictions(data);
                }
            })
            .catch(err => {
                setLoading(false);  // Stop loading
                console.error('Error fetching predictions:', err);
                setError('Error fetching predictions. Please try again later.');
            });
    };

    return (
        <div className="App">
            <h1>Stock Prediction</h1>
            <label htmlFor="stockSymbol">Stock Symbol:</label>
            <input
                type="text"
                id="stockSymbol"
                value={stockSymbol}
                onChange={(e) => setStockSymbol(e.target.value)}
            />
            <button onClick={fetchStockPrediction}>Get Prediction</button>

            <h2>Predictions</h2>
            {loading ? <p>Loading...</p> : null}
            <div id="result">
                {error && <p>{error}</p>}
                {predictions.map((prediction, index) => (
                    <p key={index}>
                        Date: {new Date(prediction.prediction_date).toLocaleDateString()}, 
                        Price: {prediction.predicted_price}
                    </p>
                ))}
            </div>
        </div>
    );
}

export default App;
